import './itemCount.css'
import { useState } from 'react'

const ItemCount = ({ stock, initial, onAdd }) => {

	const [ counter, setCounter ] = useState(initial)

	const addItem = () => {
		if(counter < stock) {
			setCounter(counter + 1)
		}
	}

	const removeItem = () => {
		if(counter > initial) {
			setCounter(counter - 1)
		}
	}
	return (
		<>
			<div className="counter">{ counter }</div>
			<button></button>
			<button></button>
			<button></button>
		</>
	)
}

export default ItemCount
