import Item from './Item'
import ItemDetail from './ItemDetail'


const ItemList = ({ discos, loading, load }) => {

	const showRecords = () => {
		if(load === 'list') {
			return discos.map((disco) => {
				return loading ? 'Cargando' : <Item disco={disco} key={disco.id}/>
			})
		} else if (load === 'single') {
			return loading ? 'Cargando' : <ItemDetail disco={discos}/>
		}

	}

	return (
		<>			
			<div className="records">				
				{ loading ? 'Cargando' : showRecords() }
			</div>

		</>
	)
}

export default ItemList
