import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { toast } from 'react-toastify'

import ItemList from './ItemList'
import discos from '../discosFull.json'


const ItemListContainer = () => {  

	const [loading, setLoading] = useState(true)
	const [productos, setProductos] = useState([])
	const {id} = useParams()
	let filtered = []



	useEffect(() => {
		if(id === 'vinilos') {
					filtered = discos.filter((disco, index) => {
						return disco.format === 'vinyl'
					})
				} else if(id ==='cds') {
					filtered = discos.filter((disco, index) => {
						return disco.format === 'cd'
					})
				} else if(id ==='cassettes') {
					filtered = discos.filter((disco, index) => {

						return disco.format === 'cassette'
					})
				}	else if(id === undefined) {
					filtered = discos
				}

				console.log(typeof id)
				console.log(id)

		const promesa = new Promise((resolve, reject) => {

			setTimeout(() => {





				resolve(filtered)
			}, 2000)
		})

		promesa.then((data) => {
			setProductos(filtered)
		})
		.catch((error) => {
			toast.error('hubo un error en la carga del catálogo')
		})
		.finally(() => {
			setLoading(false)
		})
	}, [id])


	return(
		<div className="item-list-container">
			<ItemList discos={productos} loading={loading} />
		</div>
	)
}

export default ItemListContainer
